import data from '../Data'
import { useState } from 'react';

function Portfolio() {

    const projects = data.projects;

    const project = data.projects[0];
    const [selectedProject, setSelectedProject] = useState(null);

    function openProject (number) {
        setSelectedProject(projects[number])
    }

    return (
        <>
            <section className='py-12 px-6 md:px-16 bg-darkblue' id='section3'>
                {/* ... */}
                <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-2'>
                    {projects.map((project, index) => (
                        <div className='flex flex-col card' key={index} onClick={() => openProject(index)}>
                            {/* ... your project card content ... */}
                        </div>
                    ))}
                </div>
                <div className={selectedProject ? "bg-backgroundcolor2 w-full lg:w-1/2 h-screen fixed top-0 right-0 z-20 p-8 slideright" : "hidden"}>
                    <button className='text-3xl font-bold text-textcolor' onClick={() => setSelectedProject(null)}>X</button>
                    <article className='p-16 flex flex-col gap-4'>
                        {selectedProject && (
                            <>
                                <h5 className='text-3xl font-bold text-textcolor'>{selectedProject.name}</h5>
                                {/* ... rest of your detailed project view ... */}
                            </>
                        )}
                    </article>
                </div>
            </section>
        </>
    );
}

export default Portfolio;
