import data from '../Data'
import { Link } from "react-router-dom";
import { useState } from 'react';

function Portfolio() {

    const projects = data.projects;

    const [toggleProject, setToggleProject] = useState(false);

    return (
        <>
            <section className='py-12 px-6 md:px-16 bg-darkblue' id='section3'>
                <h3 className='text-3xl py-8 text-textcolor font-bold'>Projecten</h3>
                <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-2'>
                    {projects.map(projects => (
                        <Link href="/mijngroningen" to="/mijngroningen">
                        <card className='flex flex-col'>
                            <div className='grid grid-cols-2 grid-rows-5 bg-backgroundcolor2 p-4 rounded-lg gap-2 cursor-pointer text-textcolor hover:text-secondary hover:scale-95 ease-in-out duration-300'>
                                <h5 className='col-span-2 text-lg font-bold text-textcolor'>{projects.name}</h5>
                                <p className='col-span-2 text-md font-regular text-textcolor'>{projects.subtitle}</p>
                                <p className='col-start-1 col-span-2 row-span-1 flex gap-2'>
                                    {projects.tags.map((tag => (
                                        <span className='bg-primary p-1 rounded text-textcolor'>
                                            {tag}
                                        </span>
                                    )))}
                                </p>
                                <button className=' text-right col-start-2 row-start-5 col-span-2'>Bekijk project &gt;</button>
                            </div>
                        </card>
                        </Link>
                    ))}
                </div>

                <div className='hidden bg-backgroundcolor2 w-full lg:w-1/2 h-screen fixed top-0 right-0 z-20 p-8'>
                    <a className='text-3xl font-bold text-textcolor' onClick={() => setToggleProject(!toggleProject)} >{toggleProject ? "Close" : "Menu"}>X</a>
                    <article className='p-16 flex flex-col gap-4'>
                    <h5 className='text-3xl font-bold text-textcolor'>{projects[0].name}</h5>
                    <p className='col-span-2 text-md font-regular text-textcolor'>{projects[0].subtitle}</p>
                                <p className='col-start-1 col-span-2 row-span-1 flex gap-2'>
                                    {projects[0].tags.map((tag => (
                                        <span className='bg-primary p-1 rounded text-textcolor'>
                                            {tag}
                                        </span>
                                    )))}
                                </p>
                    <p className='col-span-2 text-md font-regular text-textcolor pt-8 max-w-xl'>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam volutpat vel tortor eu porta. Suspendisse potenti. Curabitur pretium finibus nisi at ultricies. Vivamus rutrum felis metus, mollis ullamcorper neque tristique quis. Fusce convallis facilisis dictum. Maecenas venenatis porta fermentum. Fusce dapibus vehicula augue sed iaculis. Ut rhoncus, leo semper aliquam semper, ante erat placerat dolor, id convallis sapien magna quis mi. Duis pretium lobortis sollicitudin. Proin accumsan nisi et erat dictum, a placerat dolor malesuada. Donec vel imperdiet orci.
                    </p>
                    </article>
                </div>
            </section>
        </>
    );
}

export default Portfolio;
